import React, {Component} from 'react';
import {connect} from "react-redux";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import * as dialogActions from "../../redux/actions/dialogActions";
import Button from "@material-ui/core/Button";
import {getTranslate} from '../locales/localeUtils';
import DialogContentText from "@material-ui/core/DialogContentText";
import TextField from "@material-ui/core/TextField";

const styles = {
  input: {
    opacity: "0",
    width: "0px",
    height: "0px"
  },
  selectFileBloc: {
    display: 'flex',
    flex: '1 1 auto',
    justifyContent: 'center',
    alignItems: 'center',
  }
};

class templateClassName extends Component {

  state = {
    databaseFile:{name:''},
    name:''
  };

  getContentText() {
    return getTranslate(this.props.locale)('IMPORT_DATABASE_TEXT');
  };

  getContent() {
    console.log(this.state.databaseFile);

    return (
      <div style={styles.selectFileBloc}>
        <TextField
          autoFocus
          margin="dense"
          id="databaseFile"
          label="Database file"
          type="txt"
          value={this.state.name}
        />
        <input
          accept="text/csv"
          style={styles.input}
          id="raised-button-file"
          type="file"
          onChange={e => {
            this.setState({databaseFile: e.target.files[0]})
          }}
        />
        <label htmlFor="raised-button-file">
          <Button variant={'contained'} component="span">
            Select File
          </Button>
        </label>
      </div>
    );
  }

  getActions() {
    let translate = getTranslate(this.props.locale);
    return [
      <Button
        key={'close_button'}
        variant="contained"
        color="secondary"
        onClick={this.handleCloseDialog}>
        {translate('CLOSE')}
      </Button>,
    ];
  }

  getTitle() {
    return (<div>{getTranslate(this.props.locale)('IMPORT_DATABASE')}</div>);
  }


  /******************************************************/
  /*Fixed part of the dialog, don't touch that          */
  /******************************************************/
  handleCloseDialog = () => {
    this.props.dispatch(dialogActions.closeDialog());
  };

  render() {
    let dialog: dialogReducerDTO = this.props.dialog;

    return (
      <Dialog open={dialog.open}>
        <DialogTitle>{this.getTitle()}</DialogTitle>
        <DialogContent>
          <DialogContentText style={{whiteSpace: 'pre-wrap'}}>
            {this.getContentText()}
          </DialogContentText>
          {this.getContent()}
        </DialogContent>
        <DialogActions>
          {this.getActions()}
        </DialogActions>
      </Dialog>
    );
  }
}

export default connect(store => {
  return {
    dialog: store.dialog,
    locale: store.locale,
  };
})(templateClassName);
